// ICONS
Context.icons={};
Context.icons.up="data:image/png;base64,";
Context.icons.down="data:image/png;base64,";
Context.icons.left="data:image/png;base64,";
Context.icons.right="data:image/png;base64,";
Context.icons.add="data:image/png;base64,";
Context.icons.remove="data:image/png;base64,";
Context.icons.edit="data:image/png;base64,";

$(document).ready(function(){
	Context.$=$(".context");
});

$(document).bind("mouseup",function(){
	if(Context.delayInterval!==false){
		clearInterval(Context.delayInterval);
		Context.delayInterval=false;
	}
	if(Context.status==true) Context.close();
});
